a = 10
b = 20

const penjumlahan = () => {
    console.log(`hasil dari ${a} + ${b} adalah = ${a + b}`);
}
penjumlahan()


const pengurangan = () => {
    console.log(`hasil dari ${a} - ${b} adalah = ${a - b}`);
}
pengurangan()


const perkalian = () => {
    console.log(`hasil dari ${a} x ${b} adalah = ${a * b}`);
}
perkalian()


const modulus = () => {
    console.log(`hasil dari ${a} % ${b} adalah = ${a % b}`);
}
modulus()


const pembagian = () => {
    console.log(`hasil dari ${a} : ${b} adalah = ${a / b}`);
}
pembagian()


const pangkat = () => {
    console.log(`hasil dari ${a} ** ${b} adalah = ${a ** b}`);
}
pangkat()
